import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Spinner from '../layout/Spinner';
import { getCurrentProfile } from '../../actions/Profile_action';
import { Link } from 'react-router-dom';

const Dashboard = ({
  getCurrentProfile,
  auth,
  profile: { profile, loading },
}) => {
  useEffect(() => {
    getCurrentProfile();
    console.log('auth:', auth);
    console.log('profile:', profile);
  }, []);
  if (loading && profile === null) {
    return <Spinner />;
  }
  return (
    <>
      <h1 className='large text-primary'>DashBoard</h1>
      <p className='lead'>
        <i className='fas fa-user'>Welcome {auth?.user && auth?.user.name}</i>
      </p>
      {profile !== null ? (
        <>has</>
      ) : (
        <>
          <p>You have not yet set a profile, please add some infor</p>
          <Link to='/create-profile' className='brn btn' />
        </>
      )}
    </>
  );
};

Dashboard.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  profile: state.profile,
});
export default connect(mapStateToProps, { getCurrentProfile })(Dashboard);
