import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const PostItem = ({ auth, post }) => {
  const { _id, text, name, avatar, user, likes, comments, data } = post;
  return (
    <div className='p-1 my-1 bg-white post'>
      <div>
        <a href='profile.html'>
          <img className='round-img' src={avatar} />
          <h4>{name}</h4>
        </a>
      </div>
      <div>
        <p className='my-1'>{text}</p>
        <p className='post-date'>Posted on {date}</p>
        <button type='button' className='btn btn-light'>
          <i className='fas fa-thumbs-up'></i>
          <span>4</span>
        </button>
        <button type='button' className='btn btn-light'>
          <i className='fas fa-thumbs-down'></i>
        </button>
        <a href='post.html' className='btn btn-primary'>
          Discussion <span className='comment-count'>2</span>
        </a>
        <button type='button' className='btn btn-danger'>
          <i className='fas fa-times'></i>
        </button>
      </div>
    </div>
  );
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, {})(PostItem);
