import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Moment from 'react-moment';
import { Link } from 'react-router-dom';
import { addLike, removeLike } from '../../actions/Post_action';

const PostItem = ({ auth, post, addLike, removeLike }) => {
  const { _id, text, name, avatar, user, likes, comments, date } = post;
  return (
    <div className='p-1 my-1 bg-white post'>
      <div>
        <a href='profile.html'>
          <img className='round-img' src={avatar} alt='avatar' />
          <h4>{name}</h4>
        </a>
      </div>
      <div>
        <p className='my-1'>{text}</p>
        <p className='post-date'>
          Posted on <Moment format='YYYY/MM/DD'>{date}</Moment>{' '}
        </p>
        <button
          type='button'
          onClick={(e) => addLike(_id)}
          className='btn btn-light'
        >
          <i className='fas fa-thumbs-up'></i>
          <span>{likes?.length > 0 && <span>{comments.length}</span>}</span>
        </button>
        <button
          type='button'
          onClick={(e) => removeLike(_id)}
          className='btn btn-light'
        >
          <i className='fas fa-thumbs-down'></i>
        </button>
        <Link to={`/post/${_id}`} className='btn btn-primary'>
          Discussion{' '}
          {comments?.length > 0 && (
            <span className='comment-count'>{comments.length}</span>
          )}
        </Link>
        {!auth?.loading && user === auth?.user?._id && (
          <button type='button' className='btn btn-danger'>
            <i className='fas fa-times'></i>
          </button>
        )}
      </div>
    </div>
  );
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, {})(PostItem);
